/****This method is used to check whether the given input year is a leap year.
 * 
 * @param yearno year accepted from user
 * @return boolean result true or false.
 */

public static boolean LeapYear(int yearno) 
{if(yearno > 1000)
{
	   if(((yearno % 4 == 0) && (yearno % 100 != 0)))
	   {
		   if((yearno % 400 == 0))
		   {
	       return true;
		   }
	   }
	}
	return false;
}

public static String checkLeapYear(int year) {  
	if (year >= 1000 && year <= 9999) {
		if (year % 400 == 0 || (year % 4 == 0 && year % 100 != 0 && year >= 1582)) {

			return "Leap Year";
		} 
		else 
		{

			return "not a Leap Year or year should be more than 1582";
		}
	} 
	else 
	{
		return "Wrong input";
	}
}

