/**This method will compute transpose of given matrix
 * @param m given matrix
 * @param r number of rows
 * @param c number of columns
 * @return transpose of matrix m
 */
public static int[][] transposeMatrix(int[][] m, int r, int c)
{
	int[][] transposeMatrix=new int[r][c];
	for(int i=0; i<r; i++)
	{
		for(int j=0; j<c; j++)
		{
			transposeMatrix[j][i]=m[i][j];
		}
	}
	
	return m;
}

/*******************Determinant of matrix************************/
 
/**This method is used to find determinant of 2X2 and 3X3 matrix.
 * @param matrix input matrix
 * @return determinant of matrix
 */
public static int findDeterminant(int[][] matrix)
{
	int sum=0; 
    int s;
    if(matrix.length==1)
    {  
      return(matrix[0][0]);
    }
    //for loop for row by row expansion..
    for(int i=0;i<matrix.length;i++) 
    { 
      int[][] smaller= new int[matrix.length-1][matrix.length-1]; 
      for(int a=1;a<matrix.length;a++)
      {
        for(int b=0;b<matrix.length;b++)
        {
          if(b<i)
          {
            smaller[a-1][b]=matrix[a][b]; //a[0][0]=matrix[1][0] & a[0][1]=matrix[1][1] & a[1][0]=matrix[2][1] & a[1][1]=matrix[2][2]
          }
          else if(b>i)
          {
            smaller[a-1][b-1]=matrix[a][b]; 
          }
        }
      }
      //To change the sign of middle element from positive to negative.
      if(i%2==0)
      { 
        s=1;
      }
      else{
        s=-1;
      }
      sum+=s*matrix[0][i]*(findDeterminant(smaller)); 
    }
    return sum; 
}

