	/**This method is used to search index of user input word from a array
	 * @param word word to search
	 * @param words array of words
	 * @param a starting index of array
	 * @param b ending index of array
	 * @return index
	 */
	public static int binarysearchWord(String word, String words[], int a, int b) 
	{
		if(b <= a)
			return -1;
		
		if(b - a == 1)
			return words[a].equals(word) ? a : -1;

		int mid = (a + b)/2;
		
		if(word.compareTo(words[mid]) < 0)
		{
			return binarysearchWord(word, words, 0, mid-1);
		}
		else if(word.compareTo(words[mid]) > 0)
		{
			return binarysearchWord(word, words, mid, b);
		}
		 return mid;
	}
	
	



/*******This method is used to perform insertion sort for string type array
 * 
 * @param str input string array
 */

public static void sortString(String[] str)
{
	int number=str.length;
	String value;
	int index;
	for (int i=1;i<number;i++)
	{
		char currentcharvalue =str[i].charAt(0);
		value= str[i];
		index=i;

        if(currentcharvalue == str[i - 1].charAt(0))
        {
            while (index > 0 && str[index - 1].charAt(1) > str[index].charAt(1))
            {
                str[index] = str[index - 1];
                index = index - 1;
            }
        }
        else
        {
            while (index > 0 && str[index - 1].charAt(0) > currentcharvalue)
            {
                str[index] = str[index - 1];
                index = index - 1;
            }
        }
        str[index] = value;
    }
}

